import {useState} from 'react'

const Button = ({handleClick, text}) => {
	return (
	<button onClick={handlerGood}>good</button>
	)

}

const Statistics = (props) => {
	if(props.data.good == 0 && props.data.neutral == 0 && props.data.bad == 0) 
	return (<div><p>No feedback given</p></div>)

	return (
		<div>
			<p>good {props.data.good}</p>
			<p>neutral {props.data.neutral}</p>
			<p>bad {props.data.bad}</p>
			<p>all {props.data.all}</p>
			<p>average {props.data.average}</p>
			<p>positive {props.data.positive} %</p>
		</div>

	)
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)
  const [average, setAverage] = useState(0)
  const [positive, setPositive] = useState(0)

  const data = {good: good, 
	neutral: neutral, 
	bad: bad, 
	all: all, 
	average: average, 
	positive: positive}

  const handlerGood = () => {
	setGood(good+1)
	setAll(good+bad+neutral+1)
	setAverage((good+1-bad)/(good+bad+neutral+1))
	setPositive((good+1)/(good+bad+neutral+1)*100)
}
  const handlerNeutral = () => {
	setNeutral(neutral+1)
	setAll(good+bad+neutral+1)
	setAverage((good-bad)/(good+bad+neutral+1))
	setPositive((good)/(good+bad+neutral+1)*100)
}
  const handlerBad = () => {
	setBad(bad+1)
	setAll(good+bad+neutral+1)
	setAverage((good-bad-1)/(good+bad+neutral+1))
	setPositive((good)/(good+bad+neutral+1)*100)
}


  return (
    <div>
		<h1>five feedback</h1>
		<Button handleClick={handerGood} text='good'/>

		<button onClick={handlerNeutral}>neutral</button>
		<button onClick={handlerBad}>bad</button>

		<h1>Statistics</h1>

		<Statistics data={data}/>



    </div>
  )
}

export default App;
