import {useState} from 'react'


const Statistics = (props) => {

	return (
		<div>
			<p>good {good}</p>
			<p>neutral {neutral}</p>
			<p>bad {bad}</p>
			<p>all {all}</p>
			<p>average {average}</p>
			<p>positive {positive} %</p>
		</div>

	)
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)
  const [average, setAverage] = useState(0)
  const [positive, setPositive] = useState(0)

  const data = {good, neutral, bad, all, average, positive}

  const handlerGood = () => {
	setGood(good+1)
	setAll(good+bad+neutral+1)
	setAverage((good+1-bad)/(good+bad+neutral+1))
	setPositive((good+1)/(good+bad+neutral+1)*100)
}
  const handlerNeutral = () => {
	setNeutral(neutral+1)
	setAll(good+bad+neutral+1)
	setAverage((good-bad)/(good+bad+neutral+1))
	setPositive((good)/(good+bad+neutral+1)*100)
}
  const handlerBad = () => {
	setBad(bad+1)
	setAll(good+bad+neutral+1)
	setAverage((good-bad-1)/(good+bad+neutral+1))
	setPositive((good)/(good+bad+neutral+1)*100)
}


  return (
    <div>
		<h1>five feedback</h1>
		<button onClick={handlerGood}>good</button>
		<button onClick={handlerNeutral}>neutral</button>
		<button onClick={handlerBad}>bad</button>

		<h1>Statistics</h1>

		<Statistics data={data}/>



    </div>
  )
}

export default App;
